"use strict";(self.webpackChunk_N_E=self.webpackChunk_N_E||[]).push([[4230],{4230:function(n,t,h){h.d(t,{AI:function(){return a},_1:function(){return i},pv:function(){return c},x6:function(){return g},xK:function(){return e},zB:function(){return o}});let i=[{name:"Năm hoạt động",num:5},{name:"Th\xe0nh vi\xean",num:50},{name:"N\xfat v\xe0ng",num:5},{name:"N\xfat bạc",num:5}],g=[{image:"https://firebasestorage.googleapis.com/v0/b/sanomedia-4959a.appspot.com/o/sanolife-2.jpg?alt=media&token=20f283b4-87f9-47dd-87cc-d2ab55c3f47f",title:"Chương tr\xecnh hoạt động: Gala Dinner",date:"05-02-2024",subText:"Một đ\xeam tiệc đầy niềm vui v\xe0 \xfd nghĩa khi to\xe0n thể nh\xe2n sự C\xf4ng ty c\xf9ng gia đ\xecnh c\xf9ng nhau nh\xecn lại h\xe0nh tr\xecnh một năm đầy biến động nhưng cũng kh\xf4ng k\xe9m phần rực rỡ của Sano Media. Để rồi sau đ\xf3 những giải thưởng vinh danh c\xe1c c\xe1 nh\xe2n, tập thể xuất sắc trong năm qua đ\xe3 được h\xf4 vang.",link:"/blog/gala-dinner"},{image:"https://firebasestorage.googleapis.com/v0/b/sanomedia-4959a.appspot.com/o/sanolife-4.jpg?alt=media&token=b972dbd6-ca89-43a7-816a-6e2496bd8af3",title:"Chương tr\xecnh hoạt động: Team building",date:"19-10-2023",subText:"L\xe0m việc tại Sano Media kh\xf4ng chỉ được khen thưởng thường xuy\xean, m\xe0 c\xf2n c\xf3 những hoạt động trải nghiệm, team building đầy cảm x\xfac. Mỗi hoạt động mang theo 1 gi\xe1 trị v\xe0 cũng l\xe0 dịp gi\xfap c\xe1c th\xe0nh vi\xean hiểu nhau hơn, th\xeam gắn kết hơn.",link:"/blog/team-building"},{image:"https://firebasestorage.googleapis.com/v0/b/sanomedia-4959a.appspot.com/o/sanolife-13.jpg?alt=media&token=7c26d99d-4b3c-4a27-aff5-e90d9a8d2ec0",title:"Chương tr\xecnh hoạt động: Chạy Marathon",date:"19-08-2023",link:"/blog/marathon",subText:"Kh\xf4ng chỉ l\xe0 một c\xf4ng ty, ch\xfang ta l\xe0 một đại gia đ\xecnh đang c\xf9ng nhau trải qua những khoảnh khắc đ\xe1ng nhớ v\xe0 th\xfa vị! Vừa qua, SANO Media Việt Nam đ\xe3 tổ chức một chuyến đi hoạt động Team Building kết hợp chạy Marathon 21km tại Hạ Long."}],c=[{image:"https://firebasestorage.googleapis.com/v0/b/sanomedia-prod.appspot.com/o/images%2F2512%2FScreenshot%202024-12-25%20085234.png?alt=media&token=3a2021a1-e1b0-45fe-a40b-882f27401cf7",title:"Tặng Qu\xe0 Gi\xe1ng Sinh: Mang Lại Kh\xf4ng Kh\xed Ấm \xc1p v\xe0 Trẻ Trung",date:"25-12-2024",subText:"M\xf9a Gi\xe1ng Sinh đến cũng l\xe0 thời điểm c\xe1c c\xf4ng ty tổ chức những hoạt động nhằm tri \xe2n v\xe0 ghi nhận những đ\xf3ng g\xf3p của nh\xe2n vi\xean trong suốt một năm l\xe0m việc vất vả. Trong số đ\xf3, việc tặng qu\xe0 Gi\xe1ng Sinh cho nh\xe2n vi\xean l\xe0 một truyền thống kh\xf4ng thể thiếu, mang lại kh\xf4ng kh\xed ấm \xe1p v\xe0 trẻ trung cho to\xe0n thể c\xf4ng ty.",link:"/blog/25-12"},{image:"https://firebasestorage.googleapis.com/v0/b/sanomedia-prod.appspot.com/o/images%2F2512%2FScreenshot%202024-12-25%20085234.png?alt=media&token=3a2021a1-e1b0-45fe-a40b-882f27401cf7",title:"Trao Thưởng KPI Đầy Ấn Tượng Cho Team Nh\xe2n Vi\xean Xuất Sắc",date:"24-12-2024",subText:"Trong một buổi chiều cuối năm đầy h\xe2n hoan, C\xf4ng ty đ\xe3 tổ chức lễ trao thưởng cho những đội ngũ nh\xe2n vi\xean xuất sắc li\xean tục đạt được KPI trong năm qua. Sự kiện diễn ra tại hội trường ch\xednh của c\xf4ng ty, với sự tham dự của ban l\xe3nh đạo v\xe0 to\xe0n thể nh\xe2n vi\xean.",link:"/blog/24-12"},{image:"https://firebasestorage.googleapis.com/v0/b/sanomedia-prod.appspot.com/o/images%2F2112%2FIMG_3007.jpg?alt=media&token=a56f0a75-a8bf-40df-89c7-393df5f8317c",title:"Chuyến Thăm Qu\xea B\xe1c Hồ Của C\xf4ng Ty V\xe0o Buổi Chiều",date:"21-12-2024",subText:"V\xe0o buổi chiều một ng\xe0y th\xe1ng 8, dưới \xe1nh nắng dịu nhẹ v\xe0 gi\xf3 m\xe1t từ c\xe1nh đồng l\xfaa, đo\xe0n xe của c\xf4ng ty ch\xfang t\xf4i bắt đầu h\xe0nh tr\xecnh về qu\xea B\xe1c Hồ, l\xe0ng Sen, x\xe3 Kim Li\xean, huyện Nam Đ\xe0n, tỉnh Nghệ An. Đ\xe2y kh\xf4ng chỉ l\xe0 một chuyến tham quan m\xe0 c\xf2n l\xe0 một h\xe0nh tr\xecnh t\xecm về cội nguồn, để mỗi nh\xe2n vi\xean hiểu hơn về cuộc đời v\xe0 sự nghiệp của Chủ tịch Hồ Ch\xed Minh, vị l\xe3nh tụ k\xednh y\xeau của d\xe2n tộc.",link:"/blog/21-12"},{image:"https://firebasestorage.googleapis.com/v0/b/sanomedia-prod.appspot.com/o/images%2F2012%2F01XL9208.JPG?alt=media&token=e4cbaa95-b013-440f-87fb-76ae666c25a6",title:"Tinh Thần Chơi Game Trong Buổi Đ\xe0o Tạo C\xf4ng Ty",date:"20-12-2024",subText:"Tinh thần chơi game đ\xe3 trở th\xe0nh một yếu tố quan trọng trong c\xe1c buổi đ\xe0o tạo c\xf4ng ty hiện đại. C\xe1c hoạt động n\xe0y kh\xf4ng chỉ mang lại niềm vui v\xe0 giảm căng thẳng m\xe0 c\xf2n gi\xfap x\xe2y dựng tinh thần đồng đội v\xe0 ph\xe1t triển kỹ năng quan trọng cho nh\xe2n vi\xean.",link:"/blog/20-12"},{image:"https://firebasestorage.googleapis.com/v0/b/sanomedia-prod.appspot.com/o/images%2F1912%2F01XL8982.JPG?alt=media&token=850e5eb1-0d2e-4656-bcbc-0b4cb5c59175",title:"Tr\xf2 Chơi X\xe2y Dựng Đội Ngũ Trong Buổi Đ\xe0o Tạo C\xf4ng Ty",date:"19-12-2024",subText:"Trong buổi đ\xe0o tạo gần đ\xe2y tại c\xf4ng ty, ch\xfang t\xf4i đ\xe3 triển khai tr\xf2 chơi 'Vẽ cờ v\xe0 slogan'. Đ\xe2y l\xe0 một hoạt động ngo\xe0i trời gi\xfap tăng cường tinh thần đồng đội v\xe0 ph\xe1t triển kỹ năng l\xe3nh đạo. H\xe3y c\xf9ng t\xecm hiểu xem tr\xf2 chơi n\xe0y đ\xe3 mang lại những gi\xe1 trị g\xec cho nh\xe2n vi\xean.",link:"/blog/19-12"},{image:"https://firebasestorage.googleapis.com/v0/b/sanomedia-prod.appspot.com/o/images%2F1812%2F01XL9749.JPG?alt=media&token=d8531a63-f1ea-4b2e-8e4e-043cd870d03b",title:"Vinh Danh Team Chiến Thắng - Team Chất",date:"18-12-2024",subText:"V\xe0o ng\xe0y 9 th\xe1ng 12, c\xf4ng ty ch\xfang ta đ\xe3 tổ chức một buổi đ\xe0o tạo th\xe0nh c\xf4ng với sự tham gia của tất cả c\xe1c ph\xf2ng ban. Trong buổi đ\xe0o tạo, c\xe1c th\xe0nh vi\xean đ\xe3 được chia th\xe0nh nhiều nh\xf3m để tham gia một tr\xf2 chơi th\xfa vị nhằm th\xfac đẩy kỹ năng l\xe0m việc nh\xf3m v\xe0 s\xe1ng tạo.",link:"/blog/18-12"},{image:"https://firebasestorage.googleapis.com/v0/b/sanomedia-prod.appspot.com/o/images%2F1712%2F2024_12_17_08_15_IMG_3279.JPG?alt=media&token=282c054b-e39f-4545-a968-fe38afc4d4cc",title:"Những Team Tiếp Tục Nhận Thưởng Ng\xe0y – B\xed Quyết Th\xe0nh C\xf4ng",date:"17-12-2024",subText:"Trong bất kỳ doanh nghiệp n\xe0o, việc khen thưởng kh\xf4ng chỉ mang \xfd nghĩa ghi nhận sự nỗ lực m\xe0 c\xf2n l\xe0 yếu tố th\xfac đẩy hiệu suất l\xe0m việc. Những team li\xean tục nhận thưởng ng\xe0y tại c\xf4ng ty ch\xednh l\xe0 minh chứng cho sự phối hợp hiệu quả v\xe0 tinh thần l\xe0m việc xuất sắc. H\xe3y c\xf9ng t\xecm hiểu b\xed quyết th\xe0nh c\xf4ng của họ qua b\xe0i viết dưới đ\xe2y.",link:"/blog/17-12"},{image:"https://firebasestorage.googleapis.com/v0/b/sanomedia-prod.appspot.com/o/images%2F1612%2FUntitled123456789.png?alt=media&token=d6f539d9-f2f2-4d80-b081-9df44b54fb03",title:"Khởi Đầu Tuần Mới Với C\xe1c Tr\xf2 Chơi Đồng Đội",date:"16-12-2024",subText:"Thứ hai kh\xf4ng nhất thiết phải l\xe0 ng\xe0y uể oải v\xe0 trở lại c\xf4ng việc một c\xe1ch miễn cưỡng. Bằng c\xe1ch đưa c\xe1c tr\xf2 chơi đồng đội v\xe0o th\xf3i quen của c\xf4ng ty, bạn c\xf3 thể tiếp th\xeam năng lượng cho nh\xe2n vi\xean v\xe0 tạo ra một tinh thần hợp t\xe1c ngay từ đầu tuần.",link:"/blog/16-12"},{image:"https://firebasestorage.googleapis.com/v0/b/sanomedia-prod.appspot.com/o/images%2F1312%2F2024_12_13_08_19_IMG_3248.JPG?alt=media&token=4d49fd06-be20-4455-a656-9eeb27e871c7",title:"Nhận Thưởng Hằng Ng\xe0y tại C\xf4ng Ty",date:"13-12-2024",subText:"Trong m\xf4i trường kinh doanh hiện đại, việc duy tr\xec động lực l\xe0m việc cho nh\xe2n vi\xean l\xe0 một trong những yếu tố quyết định sự th\xe0nh c\xf4ng của doanh nghiệp. Ch\xednh s\xe1ch nhận thưởng hằng ng\xe0y l\xe0 một giải ph\xe1p hiệu quả,vừa gi\xfap n\xe2ng cao tinh thần l\xe0m việc vừa cải thiện hiệu suất tổng thể.",link:"/blog/13-12"},{image:"https://firebasestorage.googleapis.com/v0/b/sanomedia-prod.appspot.com/o/images%2F1212%2F2024_12_11_16_31_IMG_3232.JPG?alt=media&token=d6434130-9012-4ece-91ab-9572e8ff57f0",title:"T\xf4n Vinh Những Nh\xe2n Vi\xean Xuất Sắc",date:"12-12-2024",subText:"Trong m\xf4i trường c\xf4ng việc hiện đại, việc c\xf4ng nhận v\xe0 khen thưởng những nỗ lực của nh\xe2n vi\xean kh\xf4ng chỉ tạo động lực m\xe0 c\xf2n đ\xf3ng g\xf3p v\xe0o sự ph\xe1t triển bền vững của c\xf4ng ty. Việc trao thưởng nh\xe2n vi\xean xuất sắc h\xe0ng th\xe1ng l\xe0 một chiến lược hiệu quả để th\xfac đẩy tinh thần l\xe0m việc v\xe0 khuyến kh\xedch nh\xe2n vi\xean đạt được những th\xe0nh t\xedch tốt hơn.",link:"/blog/12-12"},{image:"https://firebasestorage.googleapis.com/v0/b/sanomedia-prod.appspot.com/o/images%2F1112%2F01XL8936.JPG?alt=media&token=ddbbed67-bf9a-4aeb-83a8-b8db2482747c",title:"Chơi Game Trong Buổi Đ\xe0o Tạo: Cải Thiện Kỹ Năng, Tinh Thần Đồng Đội",date:"11-12-2024",subText:"Trong m\xf4i trường l\xe0m việc hiện đại ng\xe0y nay, việc kết hợp giữa học hỏi v\xe0 giải tr\xed kh\xf4ng chỉ gi\xfap n\xe2ng cao hiệu quả c\xf4ng việc m\xe0 c\xf2n tạo ra một kh\xf4ng kh\xed l\xe0m việc thoải m\xe1i, năng động. Một trong những phương ph\xe1p đang được nhiều c\xf4ng ty \xe1p dụng trong c\xe1c buổi đ\xe0o tạo ch\xednh l\xe0 chơi game. Vậy, chơi game trong buổi đ\xe0o tạo của c\xf4ng ty mang lại những lợi \xedch g\xec? H\xe3y c\xf9ng t\xecm hiểu trong b\xe0i viết n\xe0y.",link:"/blog/11-12"},{image:"https://firebasestorage.googleapis.com/v0/b/sanomedia-prod.appspot.com/o/images%2F1012%2F01XL9932.JPG?alt=media&token=14a1019b-36cf-4cf8-9a72-5240011175f5",title:"Chương Tr\xecnh Đ\xe0o Tạo - Ph\xe1t Triển Kỹ Năng, Th\xfac Đẩy Sự Nghiệp",date:"10-12-2024",subText:"Tại Sano, ch\xfang t\xf4i lu\xf4n coi việc đ\xe0o tạo v\xe0 ph\xe1t triển nh\xe2n vi\xean l\xe0 một yếu tố then chốt để x\xe2y dựng một đội ngũ vững mạnh. Chương tr\xecnh đ\xe0o tạo của ch\xfang t\xf4i kh\xf4ng chỉ tập trung v\xe0o kỹ năng chuy\xean m\xf4n m\xe0 c\xf2n ph\xe1t triển c\xe1c kỹ năng mềm cần thiết cho sự nghiệp l\xe2u d\xe0i. H\xe3y c\xf9ng t\xecm hiểu về c\xe1c chương tr\xecnh đ\xe0o tạo đặc biệt tại c\xf4ng ty ch\xfang t\xf4i, những lợi \xedch m\xe0 bạn sẽ nhận được khi tham gia v\xe0 l\xfd do v\xec sao đ\xe2y l\xe0 một cơ hội tuyệt vời để ph\xe1t triển nghề nghiệp.",link:"/blog/10-12"},{image:"https://firebasestorage.googleapis.com/v0/b/sanomedia-prod.appspot.com/o/images%2F712%2F2024_11_30_08_20_IMG_3114.JPG?alt=media&token=d1f8c5d6-9ef6-450f-af96-784abb1c2e7d",title:"Tổ Chức Sinh Nhật Cho Nh\xe2n Vi\xean: Tăng Cường Gắn Kết",date:"07-12-2024",subText:"Sinh nhật l\xe0 một dịp đặc biệt trong cuộc đời mỗi người, v\xe0 đối với nh\xe2n vi\xean trong một c\xf4ng ty, việc tổ chức sinh nhật c\xf3 thể l\xe0 một c\xe1ch tuyệt vời để thể hiện sự quan t\xe2m, t\xf4n trọng v\xe0 x\xe2y dựng văn h\xf3a c\xf4ng ty gắn kết. Việc tổ chức sinh nhật cho nh\xe2n vi\xean kh\xf4ng chỉ gi\xfap họ cảm thấy được đ\xe1nh gi\xe1 cao m\xe0 c\xf2n đ\xf3ng g\xf3p v\xe0o một m\xf4i trường l\xe0m việc t\xedch cực, nơi m\xe0 mọi người cảm thấy vui vẻ, động vi\xean v\xe0 c\xf3 động lực l\xe0m việc hơn.",link:"/blog/7-12"},{image:"https://firebasestorage.googleapis.com/v0/b/sanomedia-prod.appspot.com/o/images%2F612%2FScreenshot%202024-12-06%20093833.png?alt=media&token=566fe9d7-f4e1-4f10-b647-8b0323819cff",title:"Tuy\xean bố đầu giờ của c\xf4ng ty",date:"06-12-2024",subText:"Tuy\xean bố đầu giờ l\xe0 một phần quan trọng trong văn h\xf3a v\xe0 chiến lược hoạt động của bất kỳ c\xf4ng ty n\xe0o. Đ\xe2y l\xe0 một cơ hội tuyệt vời để khởi động ng\xe0y l\xe0m việc mới, tạo động lực cho nh\xe2n vi\xean v\xe0 nhấn mạnh những mục ti\xeau trọng yếu. Vậy tuy\xean bố đầu giờ c\xf3 vai tr\xf2 như thế n\xe0o v\xe0 l\xe0m thế n\xe0o để thực hiện hiệu quả? C\xf9ng t\xecm hiểu ngay trong b\xe0i viết n\xe0y!",link:"/blog/6-12"},{image:"https://firebasestorage.googleapis.com/v0/b/sanomedia-prod.appspot.com/o/images%2F512%2FSequence%2001_152.jpg?alt=media&token=555647c0-44c6-42fe-857a-2f3c88084d4b",title:"Tập Thể Dục Đầu Giờ: Giải Ph\xe1p Tăng Cường Sức Khỏe V\xe0 Năng Suất C\xf4ng Việc",date:"05-12-2024",subText:"Trong m\xf4i trường c\xf4ng sở hiện đại, sức khỏe v\xe0 tinh thần của nh\xe2n vi\xean ng\xe0y c\xe0ng trở n\xean quan trọng. Một trong những phương ph\xe1p hiệu quả để cải thiện cả thể chất v\xe0 tinh thần l\xe0 tập thể dục đầu giờ. Hoạt động n\xe0y kh\xf4ng chỉ gi\xfap nh\xe2n vi\xean duy tr\xec sức khỏe m\xe0 c\xf2n n\xe2ng cao hiệu quả c\xf4ng việc v\xe0 tạo ra một kh\xf4ng kh\xed l\xe0m việc t\xedch cực. H\xe3y c\xf9ng t\xecm hiểu những lợi \xedch v\xe0 c\xe1ch tổ chức hoạt động thể dục đầu giờ trong c\xf4ng ty.",link:"/blog/5-12"},{image:"https://firebasestorage.googleapis.com/v0/b/sanomedia-prod.appspot.com/o/images%2F412%2Fz6095572829177_571f00a9e2280a2a1c7ba17ae9f84c0c.jpg?alt=media&token=7026f9ef-7e75-4773-bd0c-04a19cf4f44a",title:"Tổ Chức Sinh Nhật Nh\xe2n Vi\xean: Lan Toả Niềm Vui Gắn Kết",date:"04-12-2024",subText:"H\xf4m nay, c\xf4ng ty vui mừng tổ chức sinh nhật cho c\xe1c bạn c\xf3 ng\xe0y sinh trong ng\xe0y h\xf4m nay! Đ\xe2y kh\xf4ng chỉ l\xe0 dịp để c\xf9ng nhau chia sẻ niềm vui, m\xe0 c\xf2n l\xe0 cơ hội gắn kết c\xe1c th\xe0nh vi\xean trong đại gia đ\xecnh Sano.",link:"/blog/4-12"},{image:"https://firebasestorage.googleapis.com/v0/b/sanomedia-prod.appspot.com/o/images%2F312%2F2024_12_03_08_15_IMG_3133.JPG?alt=media&token=fca7625b-fc4e-4a3d-a950-314e6b0ef4fc",title:"Khen Thưởng Đội Ngũ Xuất Sắc Trong Ng\xe0y",date:"03-12-2024",subText:"Trong mỗi tổ chức, đội ngũ nh\xe2n vi\xean l\xe0 yếu tố quan trọng quyết định sự th\xe0nh c\xf4ng v\xe0 ph\xe1t triển bền vững. Để ghi nhận những đ\xf3ng g\xf3p xuất sắc v\xe0 kh\xedch lệ tinh thần l\xe0m việc của nh\xe2n vi\xean, việc tổ chức khen thưởng l\xe0 một hoạt động kh\xf4ng thể thiếu. V\xe0o ng\xe0y đặc biệt n\xe0y, c\xf4ng ty ch\xfang t\xf4i đ\xe3 tổ chức lễ khen thưởng cho đội ngũ nh\xe2n vi\xean xuất sắc, những người đ\xe3 cống hiến kh\xf4ng ngừng nghỉ để đưa c\xf4ng ty ph\xe1t triển mạnh mẽ hơn.",link:"/blog/3-12"},{image:"https://firebasestorage.googleapis.com/v0/b/sanomedia-prod.appspot.com/o/images%2F2711%2F2024_11_27_08_18_IMG_3087.png?alt=media&token=79940917-bd47-472f-b06a-0d50b73e538d",title:"Nhận thưởng hằng ng\xe0y",date:"27-11-2024",subText:"Nhận thưởng hằng ng\xe0y tại c\xf4ng ty kh\xf4ng chỉ l\xe0 một ch\xednh s\xe1ch kh\xedch lệ m\xe0 c\xf2n l\xe0 c\xe1ch hiệu quả để th\xfac đẩy tinh thần l\xe0m việc, tạo động lực v\xe0 x\xe2y dựng m\xf4i trường doanh nghiệp năng động. Vậy tại sao nhận thưởng hằng ng\xe0y lại quan trọng, v\xe0 l\xe0m sao để \xe1p dụng hiệu quả? H\xe3y c\xf9ng t\xecm hiểu qua b\xe0i viết n\xe0y!",link:"/blog/2711"},{image:"/image/2010/image11.webp",title:"Chương Tr\xecnh Party Ng\xe0y 20/10",date:"20-10-2024",subText:"Ng\xe0y 20/10 kh\xf4ng chỉ l\xe0 dịp đặc biệt để t\xf4n vinh phụ nữ Việt Nam m\xe0 c\xf2n l\xe0 cơ hội để c\xe1c c\xf4ng ty thể hiện sự tri \xe2n v\xe0 ghi nhận đ\xf3ng g\xf3p của những “b\xf4ng hoa” rực rỡ trong đội ngũ nh\xe2n sự. Năm nay, c\xf4ng ty ch\xfang t\xf4i đ\xe3 tổ chức một bữa tiệc ấm \xe1p v\xe0 đầy m\xe0u sắc để mừng ng\xe0y Phụ nữ Việt Nam, mang đến những kỷ niệm đ\xe1ng nhớ cho to\xe0n thể nh\xe2n vi\xean.",link:"/blog/20-10"},{image:"/image/cuu-tro/image8.webp",title:"H\xe0nh tr\xecnh cứu trợ: Hiệp Ho\xe0 - Bắc Giang",date:"12-09-2024",subText:"Trong tinh thần “l\xe1 l\xe0nh đ\xf9m l\xe1 r\xe1ch,” c\xf4ng ty ch\xfang t\xf4i đ\xe3 tổ chức chuyến đi cứu trợ v\xf9ng lũ với mong muốn chia sẻ kh\xf3 khăn, gi\xfap đỡ đồng b\xe0o tại c\xe1c khu vực chịu ảnh hưởng nặng nề từ thi\xean tai. Đ\xe2y kh\xf4ng chỉ l\xe0 hoạt động mang t\xednh nh\xe2n văn m\xe0 c\xf2n thể hiện tr\xe1ch nhiệm x\xe3 hội của doanh nghiệp, g\xf3p phần lan tỏa những gi\xe1 trị t\xedch cực đến cộng đồng.",link:"/blog/cuu-tro"},{image:"/image/cua-lo/image14.webp",title:"Du lịch hằng năm: Cửa L\xf2 - Nghệ An",date:"14-08-2024",subText:"Chuyến du lịch thường ni\xean lu\xf4n l\xe0 dịp để c\xe1c th\xe0nh vi\xean trong c\xf4ng ty c\xf9ng nhau trải nghiệm những gi\xe2y ph\xfat thư gi\xe3n, vui vẻ b\xean nhau. Năm nay, c\xf4ng ty ch\xfang t\xf4i đ\xe3 chọn Cửa L\xf2 - một trong những b\xe3i biển đẹp v\xe0 nổi tiếng nhất khu vực miền Trung, l\xe0m điểm đến. H\xe3y c\xf9ng kh\xe1m ph\xe1 những khoảnh khắc đ\xe1ng nhớ trong chuyến h\xe0nh tr\xecnh n\xe0y!",link:"/blog/cua-lo"},{image:"https://firebasestorage.googleapis.com/v0/b/sanomedia-4959a.appspot.com/o/bonus%2Fimage1.jpg?alt=media&token=8551ac58-7729-4569-997e-80fa59b0c74a",title:"Chương Tr\xecnh Khen Thưởng: T\xf4n Vinh Những Nỗ Lực Kh\xf4ng Ngừng",date:"22-05-2024",subText:"Khen thưởng l\xe0 một phần kh\xf4ng thể thiếu trong văn h\xf3a doanh nghiệp của Sano Media. Đ\xe2y kh\xf4ng chỉ l\xe0 c\xe1ch để c\xf4ng ty ghi nhận v\xe0 t\xf4n vinh những nỗ lực v\xe0 th\xe0nh t\xedch của nh\xe2n vi\xean m\xe0 c\xf2n l\xe0 động lực th\xfac đẩy tinh thần l\xe0m việc v\xe0 sự cống hiến của to\xe0n bộ đội ngũ. Chương tr\xecnh khen thưởng định kỳ đ\xe3 trở th\xe0nh một hoạt động quan trọng, mang lại nhiều gi\xe1 trị t\xedch cực cho cả c\xf4ng ty v\xe0 nh\xe2n vi\xean.",link:"/blog/bonus"},{image:"https://firebasestorage.googleapis.com/v0/b/sanomedia-4959a.appspot.com/o/sanolife-2.jpg?alt=media&token=20f283b4-87f9-47dd-87cc-d2ab55c3f47f",title:"Chương tr\xecnh hoạt động: Gala Dinner",date:"05-02-2024",subText:"Một đ\xeam tiệc đầy niềm vui v\xe0 \xfd nghĩa khi to\xe0n thể nh\xe2n sự C\xf4ng ty c\xf9ng gia đ\xecnh c\xf9ng nhau nh\xecn lại h\xe0nh tr\xecnh một năm đầy biến động nhưng cũng kh\xf4ng k\xe9m phần rực rỡ của Sano Media. Để rồi sau đ\xf3 những giải thưởng vinh danh c\xe1c c\xe1 nh\xe2n, tập thể xuất sắc trong năm qua đ\xe3 được h\xf4 vang.",link:"/blog/gala-dinner"},{image:"https://firebasestorage.googleapis.com/v0/b/sanomedia-4959a.appspot.com/o/sanolife-4.jpg?alt=media&token=b972dbd6-ca89-43a7-816a-6e2496bd8af3",title:"Chương tr\xecnh hoạt động: Team building",date:"19-10-2023",subText:"L\xe0m việc tại Sano Media kh\xf4ng chỉ được khen thưởng thường xuy\xean, m\xe0 c\xf2n c\xf3 những hoạt động trải nghiệm, team building đầy cảm x\xfac. Mỗi hoạt động mang theo 1 gi\xe1 trị v\xe0 cũng l\xe0 dịp gi\xfap c\xe1c th\xe0nh vi\xean hiểu nhau hơn, th\xeam gắn kết hơn.",link:"/blog/team-building"},{image:"https://firebasestorage.googleapis.com/v0/b/sanomedia-4959a.appspot.com/o/sanolife-13.jpg?alt=media&token=7c26d99d-4b3c-4a27-aff5-e90d9a8d2ec0",title:"Chương tr\xecnh hoạt động: Chạy Marathon",date:"19-08-2023",subText:"Kh\xf4ng chỉ l\xe0 một c\xf4ng ty, ch\xfang ta l\xe0 một đại gia đ\xecnh đang c\xf9ng nhau trải qua những khoảnh khắc đ\xe1ng nhớ v\xe0 th\xfa vị! Vừa qua, SANO Media Việt Nam đ\xe3 tổ chức một chuyến đi hoạt động Team Building kết hợp chạy Marathon 21km tại Hạ Long.",link:"/blog/marathon"},{image:"https://firebasestorage.googleapis.com/v0/b/sanomedia-4959a.appspot.com/o/sanolife-14.jpg?alt=media&token=21ccb35e-6a1c-4bee-9fdc-4a67bf3ae187",title:"Chương tr\xecnh hoạt động: Đ\xe0o tạo nội bộ",date:"05-03-2023",subText:"Sano Media c\xf3 tổ chức hoạt động đ\xe0o tạo nội bộ thường ni\xean cho to\xe0n bộ th\xe0nh vi\xean trong C\xf4ng ty nhằm n\xe2ng cao kiến thức, nhận thức cho mọi người.",link:"/blog/training"}],a={editor:{salary:"12-15M",quantity:"3",date:"31/12/2024",title:"Editor",description:["Dựng video, cắt gh\xe9p c\xe1c video để tạo th\xe0nh 1 video ho\xe0n chỉnh theo kịch bản c\xf3 sẵn , nguồn c\xf3 sẵn.","Định hướng nội dung v\xe0 h\xecnh ảnh trong video\n(Bi\xean tập, chỉnh sửa video lồng gh\xe9p \xe2m thanh, h\xecnh ảnh, hiệu ứng...cho ph\xf9 hợp)"],required:["Nam/Nữ tuổi từ 20-25","Biết sử dụng cơ bản một số phần mềm chỉnh sửa video: phần mềm Adobe Premiere, PTS ….","Tư duy s\xe1ng tạo, nắm bắt c\xe1c xu hướng nội dung video","L\xe0m việc cẩn thận chỉn chu."],treatment:["Thu nhập trung b\xecnh 12.000.000 - 15.000.000đ/th\xe1ng","M\xf4i trường l\xe0m việc năng động, nh\xe2n sự trẻ , teamwork tốt","Được thỏa sức học tập ph\xe1t triển bản th\xe2n cũng như c\xe1c kỹ năng chuy\xean m\xf4n","Chế độ bảo hiểm, phụ cấp đầy đủ","Được cấp m\xe1y t\xednh l\xe0m việc"]},content:{salary:"12-15M",quantity:"3",date:"31/12/2024",title:"Content",description:["T\xecm nguồn, x\xe2y dựng kịch bản cho video đăng youtube.","Ph\xe1t triển v\xe0 soạn thảo c\xe1c nội dung cụ thể theo Key c\xf3 sẵn","Phối hợp với bộ phận edit để l\xean \xfd tưởng, kế hoạch x\xe2y dựng ph\xe1t triển k\xeanh","B\xe1o c\xe1o c\xf4ng việc v\xe0 hỗ trợ thực hiện c\xe1c c\xf4ng việc kh\xe1c do Quản l\xfd y\xeau cầu."],required:["Nam/Nữ tuổi từ 20-25","Ưu ti\xean Tốt nghiệp từ Cao Đẳng, Đại học","Tư duy s\xe1ng tạo, nắm bắt c\xe1c xu hướng nội dung video","L\xe0m việc cẩn thận chỉn chu."],treatment:["Mức thu nhập trung b\xecnh từ 13 -15tr/th\xe1ng","Được l\xe0m việc trong m\xf4i trường genZ trẻ, chuy\xean nghiệp, năng động","Được li\xean tục học tập ph\xe1t triển bản th\xe2n v\xe0 n\xe2ng cao kỹ năng chuy\xean m\xf4n","Được hưởng đầy đủ c\xe1c quyền lợi lao động: BHXH, BHYT, Lễ Tết,...","Được tham gia c\xe1c hoạt động teambuilding, li\xean hoan th\xe1ng/Năm …."]},manager:{title:"Quản l\xfd"},artist2D:{salary:"12-15M",quantity:"3",date:"31/12/2024",title:"Artist 2D",description:["Lĩnh vực: Youtube 2D thị trường nước ngo\xe0i với đa nội dung","Vẽ minh hoạ c\xe1c nội dung c\xf3 sẵn theo kịch bản","Định hướng nội dung, concept v\xe0 bố cục"],required:["Nam/Nữ tuổi từ 20-25","Biết sử dụng cơ bản một số phần mềm vẽ: Illustrator, Photoshop, Procreate, ….","Tư duy s\xe1ng tạo, nắm bắt c\xe1c xu hướng h\xecnh ảnh, trending.","L\xe0m việc cẩn thận chỉn chu."],treatment:["Thu nhập trung b\xecnh 12 - 15tr/th\xe1ng","M\xf4i trường l\xe0m việc năng động, nh\xe2n sự trẻ , teamwork tốt, c\xf3 nhiều cơ hội thăng tiến","Được thỏa sức học tập ph\xe1t triển bản th\xe2n cũng như c\xe1c kỹ năng chuy\xean m\xf4n","Chế độ bảo hiểm, phụ cấp đầy đủ","Được hưởng c\xe1c ch\xednh s\xe1ch ph\xfac lợi theo quy định của c\xf4ng ty","Được đ\xe0o tạo, n\xe2ng cao nghiệp vụ thường xuy\xean","Được hỗ trợ trau dồi th\xeam về chuy\xean m\xf4n v\xe0 kỹ năng li\xean quan từ ban l\xe3nh đạo c\xf4ng ty v\xe0 những người c\xf3 kinh nghiệm","Cung cấp trang thiết bị đầy đủ để phục vụ c\xf4ng việc"]},animator2D:{salary:"12-15M",quantity:"3",date:"31/12/2024",title:"Animator 2D",description:["Lĩnh vực : Youtube 2D thị trường nước ngo\xe0i với đa nội dung","Dựng hoạt h\xecnh c\xe1c nội dung c\xf3 sẵn theo kịch bản","Định hướng nội dung, concept v\xe0 bố cục, Edit hậu kỳ c\xe1c video hoạt h\xecnh 2D"],required:["Nam/Nữ tuổi từ 20-25","Biết sử dụng cơ bản một số phần mềm dựng hoạt h\xecnh: After Effect, Moho, ….","Tư duy s\xe1ng tạo, nắm bắt c\xe1c nguy\xean l\xed chuyển động cơ bản.","L\xe0m việc cẩn thận chỉn chu."],treatment:["Thu nhập trung b\xecnh 12 - 15tr/th\xe1ng","M\xf4i trường l\xe0m việc năng động, nh\xe2n sự trẻ , teamwork tốt, c\xf3 nhiều cơ hội thăng tiến","Được thỏa sức học tập ph\xe1t triển bản th\xe2n cũng như c\xe1c kỹ năng chuy\xean m\xf4n","Chế độ bảo hiểm, phụ cấp đầy đủ","Được hưởng c\xe1c ch\xednh s\xe1ch ph\xfac lợi theo quy định của c\xf4ng ty","Được hỗ trợ trau dồi th\xeam về chuy\xean m\xf4n v\xe0 kỹ năng li\xean quan từ ban l\xe3nh đạo c\xf4ng ty v\xe0\nnhững người c\xf3 kinh nghiệm","Cung cấp trang thiết bị đầy đủ để phục vụ c\xf4ng việc"]}},o="0522214201",e="sanomediavn@gmail.com"}}]);